#!/bin/bash
sudo su -
apt-get update
apt-get -y install git
rm -rf /home/bitnami/hbfl
git clone https://github.com/ryanmurakami/hbfl.git /home/bitnami/hbfl
chown -R bitnami: /home/bitnami/hbfl
cd /home/bitnami/hbfl
npm ci
sleep 5
cd /home/bitnami/hbfl
npm ci
npm run start

